import { Router } from 'express';
import { createNewProjectHasUser, getProjectsHasUsers, getProjectHasUserById, deleteProjectHasUserById, updateProjectHasUserById } from '../controllers/projectsHasUsers.controller';

const router = Router();

router.get('/api/ProjectsHasUsers', getProjectsHasUsers)

router.post('/api/ProjectsHasUsers', createNewProjectHasUser)

router.get('/api/ProjectsHasUsers/:id', getProjectHasUserById)

router.delete('/api/ProjectsHasUsers/:id', deleteProjectHasUserById)

router.put('/api/ProjectsHasUsers/:id', updateProjectHasUserById)


export default router;

