  updateProjectHasUser: "UPDATE ProjectsHasUsers\n        Set proyectsIdProject = @proyectsIdProject, \n        userIdUser = @userIdUser,\n        rolesIdRol = @rolesIdRol\n    WHERE Id = @id",
  updateUser: "UPDATE Users\n        Set userName = @userName, \n        password = @password,\n        userMail = @userMail,\n        phoneNumber = @phoneNumber\n    WHERE userId = @id"
};
